export default {
  name: 'dyn-form',
  props: {
    formData: Array
  },
  data: function data() {
    return {
      fData: this.formData,
      testVal: 'hahah'
    };
  },
  watch: {
    //props传参容易出现父组件数据变化，子组件数据没更新的问题，这里可以这样做优化
    formData: {
      immediate: true,
      deep: true,
      handler: function handler(val) {
        this.fData = val;
        this.$emit('getDataFromSub', this.testVal);
      }
    }
  },
  computed: {
    subComponent: function subComponent(component) {
      return function (component) {
        var subType = "";

        switch (component) {
          case 'el-select':
            subType = 'el-option';
            break;

          case 'el-radio-group':
            subType = 'el-radio';
            break;

          case 'el-checkbox-group':
            subType = 'el-checkbox';
            break;
        }

        return subType;
      };
    }
  },
  mounted: function mounted() {}
};
