
import mixin from './mixin'
import components from './components'
import axios from 'axios';
import  util from './util'
import  routers from './router'

const install = function(Vue,opts={}) {

  if(opts.mixin){
     Vue.mixin(mixins);
  }
  // Vue.use(Router);

  Vue.prototype.$axios=axios;
  // Vue.prototype.$getMenus=routers.getMenus;
  // Vue.prototype.$addMenus=routers.addMenus;


  for(let i in components){
   Vue.component(components[i].name,components[i])
  }

};

export const mixins=mixin;
export const {_import, _require }=util;
export const { getMenuRoutes,addMenuToRoutes,addTabsRoutes,addMainRoutes,getMenus,addMenus,pathToRoutes}=routers

export default {
    install,
    ...util,
    ...components,
    ...routers,
    mixins
  };
  